package operator;

public class ArithassignEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10,b=3;
		a+=b;//a=a+b;
		System.out.println(a);
		a-=b;
		System.out.println(a);
		a*=b;
		System.out.println(a);
		a=a/b;
		System.out.println(a);
		a=a%b;
		System.out.println(a);
		

	}

}
